import React from "react";
import { Link } from "react-router-dom";
import ReactPlayer from "react-player";

import ImageSlider from "./ImageSlider";
import Carousel from "nuka-carousel";
function CardItem({ post, slides }) {
  const containerStyles = {
    width: "500px",
    height: "280px",
  };

  return (
    <li className="cards__item">
      <ReactPlayer
        url="https://web.telegram.org/z/progressive/msg-1197363285-53370:5281016462025169076"
        width="100%"
        height="100%"
        controls={true}
      />
      <div className="cards__item__div">
        <div style={containerStyles}>
          <Carousel>
            <div
              style={{
                width: "500px",
                height: "250px",
                borderRadius: "10px",
              }}
            >
              {/* <video width="100%" height="100%" controls>
                <source src="/videos/video-2.mp4" type="video/mp4" />
              </video> */}
            </div>

            {slides.map((slide, slideIndex) => (
              <div
                style={{
                  width: "500px",
                  height: "250px",
                  borderRadius: "10px",
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundImage: `url(${slide.url})`,
                }}
              ></div>
            ))}
          </Carousel>
        </div>
        {/* <ImageSlider slides={slides} /> */}
        <Link className="cards__item__link" to={`/post/${post._id}`}>
          <div className="cards__item__info">
            <h5 className="cards__item__text">{post.desc}</h5>
          </div>
        </Link>
      </div>
    </li>
  );
}

// src="images/img-9.jpg"
// text="Explore the hidden waterfall deep inside the Amazon Jungle"
// label="Adventure"
// path="/services"

export default CardItem;
