import React from "react";
import { Link } from "react-router-dom";

import ImageSlider from "./ImageSlider";

function CardItem({ post }) {
  const slides = [
    { url: "http://placekitten.com/200/300", title: "Beach1" },
    { url: "https://picsum.photos/200/300", title: "Beach2" },
    { url: "http://placekitten.com/200/300", title: "Beach3" },
    { url: "https://picsum.photos/200/300", title: "Beach4" },
    { url: "http://placekitten.com/200/300", title: "Beach5" },
  ];

  const containerStyles = {
    width: "500px",
    height: "280px",
    margin: "0 auto",
  };

  return (
    <li className="cards__item">
      <div className="cards__item__div">
        <figure className="cards__item__pic-wrap" data-category={post.title}>
          {/* <img className="cards__item__img" alt="Travel" src={""} /> */}
          <div style={containerStyles}>
            <ImageSlider slides={slides} />
          </div>
        </figure>
        <Link className="cards__item__link" to={`/post/${post._id}`}>
          <div className="cards__item__info">
            <h5 className="cards__item__text">{post.desc}</h5>
          </div>
        </Link>
      </div>
    </li>
  );
}

// src="images/img-9.jpg"
// text="Explore the hidden waterfall deep inside the Amazon Jungle"
// label="Adventure"
// path="/services"

export default CardItem;
