import React, { useState, useEffect } from "react";
import { Button } from "./Button";
import { Link } from "react-router-dom";
import "./Navbar.css";

import ReactPlayer from "react-player";

function Navbar({ user, handleSignOut }) {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [loginData, setLoginData] = useState(
    localStorage.getItem("loginData") ? localStorage.getItem("loginData") : null
  );

  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);

  const showButton = () => {
    /* global google */
    google.accounts.id.renderButton(document.getElementById("signInDiv"), {
      type: "standard",
      shape: "circle",
      text: "continue_with",
    });
    google.accounts.id.renderButton(
      document.getElementById("signInMobileDiv"),
      {
        type: "icon",
        shape: "circle",
      }
    );
    if (window.innerWidth <= 960) {
      setButton(false);

      document.getElementById("signInDiv").hidden = true;
      document.getElementById("signInMobileDiv").hidden = false;
    } else {
      setButton(true);
      document.getElementById("signInDiv").hidden = false;
      document.getElementById("signInMobileDiv").hidden = true;
    }
  };

  useEffect(() => {
    showButton();
  }, []);

  window.addEventListener("resize", showButton);

  return (
    <nav className="navbar">
      <div
        id="playerWrapper"
        style={{
          borderRadius: "10px",
          height: "200px",
          width: "150px",
        }}
      >
        {/* <video width="100%" height="100%" controls>
                <source src="/videos/video-2.mp4" type="video/mp4" />
              </video> */}
        <ReactPlayer
          height="100%"
          width="100%"
          url="https://www.learningcontainer.com/wp-content/uploads/2020/05/sample-mp4-file.mp4"
          controls={false}
          playing
          wrapper="playerWrapper"
        />
      </div>
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
          TRVL
          <i className="fab fa-typo3" />
        </Link>
        <div className="menu-icon" onClick={handleClick}>
          <div id="signInMobileDiv" className="sign-in-mobile-div" />
          <i className={click ? "fas fa-times" : "fas fa-bars"} />
        </div>
        <ul className={click ? "nav-menu active" : "nav-menu"}>
          <li className="nav-item">
            <Link to="/" className="nav-links" onClick={closeMobileMenu}>
              Home
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/write" className="nav-links" onClick={closeMobileMenu}>
              Nová zpráva
            </Link>
          </li>
          <li className="nav-item">
            <Link
              to="/products"
              className="nav-links"
              onClick={closeMobileMenu}
            >
              Products
            </Link>
          </li>

          <li>
            <Link
              to="/login"
              className="nav-links-mobile"
              onClick={closeMobileMenu}
            >
              Sign Up
            </Link>
          </li>
        </ul>
        {user && (
          <div>
            <img src={user.picture} />
            <h3>{user.name}</h3>
          </div>
        )}
        <div id="signInDiv" />
        {Object.keys(user).length != 0 && (
          <button onClick={() => handleSignOut()}>Sign out</button>
        )}
        {/* {loginData
          ? button && <Button buttonStyle="btn--outline">SIGN UP</Button>
          : button && <Button buttonStyle="btn--outline">SIGN OUT</Button>} */}
      </div>
    </nav>
  );
}

export default Navbar;
