import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import "./Navbar.css";
import { Menu, MenuItem } from "@szhsin/react-menu";
import "@szhsin/react-menu/dist/index.css";
import "@szhsin/react-menu/dist/transitions/slide.css";
import useWindowSize from "../hooks/useWidowSize";

function Navbar({ user, handleSignOut }) {
  const [click, setClick] = useState(false);
  const [button, setButton] = useState(true);
  const [loginData, setLoginData] = useState(
    localStorage.getItem("loginData") ? localStorage.getItem("loginData") : null
  );
  const [isLoggedIn, setLoggedIn] = useState(false);
  useEffect(() => {
    setLoggedIn(Object.keys(user).length === 0);
    console.log("isLoggedIn = " + isLoggedIn);
  }, [user]);
  const handleClick = () => setClick(!click);
  const closeMobileMenu = () => setClick(false);
  const size = useWindowSize();
  const isButton = size.width > 960;

  // useEffect(
  //   (button, isLoggedIn) => {
  //     const showButton = () => {
  //       /* global google */
  //       if (isLoggedIn) {
  //         // google.accounts.id.renderButton(document.getElementById("signInDiv"), {
  //         //   type: "standard",
  //         //   shape: "circle",
  //         //   text: "continue_with",
  //         // });
  //         // google.accounts.id.renderButton(
  //         //   document.getElementById("signInMobileDiv"),
  //         //   {
  //         //     type: "icon",
  //         //     shape: "circle",
  //         //   }s
  //         // );
  //       }
  //       if (window.innerWidth < 960) {
  //         console.log("Set false");
  //         setButton(false);
  //       } else if (window.innerWidth > 960) {
  //         console.log("Set true");
  //         setButton(true);
  //       }
  //       console.log("button = " + button);
  //       console.log("window.innerWidth = " + window.innerWidth);
  //     };
  //     showButton();
  //     window.addEventListener("resize", showButton);
  //   },
  //   [button]
  // );

  const wrapperStyle = {
    position: "relative",
    paddingTop: "56.25%",
  };

  const playerStyle = {
    position: "absolute",
    top: 0,
    left: 0,
  };

  const boxStyle = {
    height: "500px",
    width: "600px",
    maxWidth: "600px",
  };
  return (
    <nav className="navbar">
      {/* <div style={boxStyle}>
        <div style={wrapperStyle}>
          <ReactPlayer
            style={playerStyle}
            height="100%"
            width="100%"
            url="test.mp4"
            controls={true}
            playing
          />
        </div>
      </div> */}
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
          VálkaUA
          {/* <i className="fab fa-typo3" /> */}
          <img src="/ukraine.png" alt="Logo" className="ukraine" />
        </Link>
        <div className="menu-icon" onClick={handleClick}>
          <div
            id="signInMobileDiv"
            className="sign-in-mobile-div"
            hidden={isButton && isLoggedIn === true}
          />
          <i className={click ? "fas fa-times" : "fas fa-bars"} />
        </div>
        <ul className={click ? "nav-menu active" : "nav-menu"}>
          <li className="nav-item">
            <Link to="/" className="nav-links" onClick={closeMobileMenu}>
              Hlavní
            </Link>
          </li>

          <li className="nav-item">
            <Link
              to=""
              className="nav-links-disabled"
              // onClick={closeMobileMenu}
            >
              Aukce
            </Link>
          </li>
          <li className="nav-item">
            <Link to="/write" className="nav-links" onClick={closeMobileMenu}>
              Nová zpráva
            </Link>
          </li>

          <li>
            <Link
              to="/login"
              className="nav-links-mobile"
              onClick={closeMobileMenu}
            >
              Sign Up
            </Link>
          </li>
        </ul>
        {!isLoggedIn && (
          <Menu
            menuButton={
              <img src={user.picture} alt="Avatar" className="user-picture" />
            }
          >
            <MenuItem disabled>
              {user.given_name + " " + user.family_name}
            </MenuItem>
            <Link to="/settings" className="context-links">
              <MenuItem>Nastavení</MenuItem>
            </Link>
            <MenuItem onClick={handleSignOut}>Odhlásit se</MenuItem>
          </Menu>
        )}

        <div id="signInDiv" hidden={!(isButton && isLoggedIn)} />
        {/* {loginData
          ? button && <Button buttonStyle="btn--outline">SIGN UP</Button>
          : button && <Button buttonStyle="btn--outline">SIGN OUT</Button>} */}
      </div>
    </nav>
  );
}

export default Navbar;
