import React from "react";
import { Link } from "react-router-dom";
import ReactPlayer from "react-player";

import ImageSlider from "./ImageSlider";
import Carousel from "nuka-carousel";
function CardItem({ post, slides }) {
  const containerStyles = {
    width: "500px",
    height: "280px",
  };

  return (
    <li className="cards__item">
      <div className="cards__item__div">
        {/* <figure className="cards__item__pic-wrap" data-category={post.title}>
           <img className="cards__item__img" alt="Travel" src={""} /> 

        </figure> */}
        <div>
          <Carousel adaptiveHeight={false}>
            {/* <div
              style={{
                width: "500px",
                height: "250px",
                borderRadius: "10px",
              }}
            > */}
            <div className="cards__player__wrapper">
              <ReactPlayer
                className="cards__react__player"
                url="https://test-videos.co.uk/vids/bigbuckbunny/mp4/h264/360/Big_Buck_Bunny_360_10s_1MB.mp4"
                width="100%"
                height="100%"
                controls={true}
                pip={true}
              />
            </div>
            {/* </div> */}

            {slides.map((slide, slideIndex) => (
              <div
                style={{
                  width: "100%",
                  height: "100%",
                  borderRadius: "10px",
                  backgroundPosition: "center",
                  backgroundSize: "cover",
                  backgroundImage: `url(${slide.url})`,
                }}
              ></div>
            ))}
          </Carousel>
        </div>
        {/* <ImageSlider slides={slides} /> */}
        <Link className="cards__item__link" to={`/post/${post._id}`}>
          <div className="cards__item__info">
            <h5 className="cards__item__text">{post.desc}</h5>
          </div>
        </Link>
      </div>
    </li>
  );
}

// src="images/img-9.jpg"
// text="Explore the hidden waterfall deep inside the Amazon Jungle"
// label="Adventure"
// path="/services"

export default CardItem;
