import React from "react";
import { Link } from "react-router-dom";
import Carousel from "./Carousel";
import ReactPlayer from "react-player";

import ImageSlider from "./ImageSlider";

function CardItem({ post }) {
  const slides = [
    {
      key: 1,
      url: "https://c4.wallpaperflare.com/wallpaper/611/154/459/widescreen-high-resolution-nature-hd-1920x1080-wallpaper-preview.jpg",
      title: "Beach1",
    },
    {
      key: 2,
      url: "https://c4.wallpaperflare.com/wallpaper/388/898/571/hd-images-nature-pc-1920x1080-wallpaper-preview.jpg",
      title: "Beach2",
    },
    {
      key: 3,
      url: "https://c4.wallpaperflare.com/wallpaper/314/778/449/1920x1080-px-los-angeles-anime-sailor-moon-hd-art-wallpaper-preview.jpg",
      title: "Beach3",
    },
    {
      key: 4,
      url: "https://c4.wallpaperflare.com/wallpaper/636/592/951/beach-1920x1080-high-definition-wallpaper-preview.jpg",
      title: "Beach4",
    },
  ];

  const containerStyles = {
    width: "500px",
    height: "280px",
  };

  return (
    <li className="cards__item">
      <div className="cards__item__div">
        {/* <figure className="cards__item__pic-wrap" data-category={post.title}>
           <img className="cards__item__img" alt="Travel" src={""} /> 

        </figure> */}
        <Carousel stopOnManual>
          {/* <div
            style={{
              width: "100%",
              height: "100%",
              borderRadius: "10px",
            }}
          >
            <ReactPlayer
              url="https://www.youtube.com/watch?v=ysz5S6PUM-U"
              width="100%"
              height="100%"
            />
          </div> */}

          {slides.map((slide, slideIndex) => (
            <div
              style={{
                width: "100%",
                height: "100%",
                borderRadius: "10px",
                backgroundPosition: "center",
                backgroundSize: "cover",
                backgroundImage: `url(${slide.url})`,
              }}
            ></div>
          ))}
        </Carousel>
        {/* <ImageSlider slides={slides} /> */}
        <Link className="cards__item__link" to={`/post/${post._id}`}>
          <div className="cards__item__info">
            <h5 className="cards__item__text">{post.desc}</h5>
          </div>
        </Link>
      </div>
    </li>
  );
}

